def out_brightness(ssn_brightness, uuid, board_name, ssn_topic, topic):
    global measure_id;
    lux_msg = "";
    lux =  random.randint(0, 1000);
    lux_msg = str(lux) + " lux";
    observation_name = "myObservation-bright-%li".replace("%li", str(measure_id));
    result_name = "myResult-bright-%li".replace("%li", str(measure_id));

    t = time.localtime()
    timestamp = (str(t.tm_year) + "-" + str(t.tm_mon) + "-" + str(t.tm_mday) + "T" + str(t.tm_hour) + ":" + str(t.tm_min) + ":" + str(t.tm_sec))

    ssn_brightness_copy = ssn_brightness.replace("%name",observation_name).replace("%result",result_name).replace("-0.1",str(lux)).replace("%s", uuid).replace("%p", board_name).replace("0000-01-01T00:00:00+00:00", timestamp);
    #ssn_brightness_copy.replace("0000-01-01T00:00:00+00:00","2019-08-12T09:15:02+02:00");

    print(lux_msg)
    print(ssn_brightness_copy)

    mqtt_client.publish(ssn_topic, ssn_brightness_copy);
    mqtt_client.publish(topic, lux_msg);

    measure_id += 1;


