def mqtt_connect(mqtt_client, userdata, flags, rc):
    mqtt_client.subscribe("led/" + DEVICE_UUID_1)


def mqtt_message(mqtt_client, userdata, msg):
    print("MESSAGE topic: " + msg.topic + "MESSAGE payload: " + msg.payload.decode())

def sensor_loop():
    while True:
        #mqtt_client.publish(SENSOR_TOPIC_1, random.randint(0, 1000))
        out_brightness(ssn_brightness1, DEVICE_UUID_1, BOARD_NAME1, BRIGHT_SSN_P1, BRIGHT_TOPIC_P1)
        out_brightness(ssn_brightness2, DEVICE_UUID_2, BOARD_NAME2, BRIGHT_SSN_P2, BRIGHT_TOPIC_P2)
        time.sleep(5)
        out_humid(ssn_humidity1, DEVICE_UUID_1, BOARD_NAME1, HUMID_SSN_P1, HUMID_TOPIC_P1);
        out_temp(ssn_temp1, DEVICE_UUID_1, BOARD_NAME1, TEMP_SSN_P1, TEMP_TOPIC_P1);
        time.sleep(5)
        out_motion(ssn_motion1, DEVICE_UUID_1, BOARD_NAME1, MOTION_SSN_P1, MOTION_TOPIC_P1, False)
        out_motion(ssn_motion2, DEVICE_UUID_2, BOARD_NAME2, MOTION_SSN_P2, MOTION_TOPIC_P2, False)
        out_motion(ssn_motion1, DEVICE_UUID_1, BOARD_NAME1, MOTION_SSN_P1, MOTION_TOPIC_P1, True)
        out_motion(ssn_motion2, DEVICE_UUID_2, BOARD_NAME2, MOTION_SSN_P2, MOTION_TOPIC_P2, True)
        time.sleep(5)

if __name__ == "__main__":
    mqtt_client = mqtt.Client()
    mqtt_client.on_connect = mqtt_connect
    mqtt_client.on_message = mqtt_message

    mqtt_client.connect(BROKER_ADDR, BROKER_PORT, 60)

    sensor_handler = threading.Thread(target=sensor_loop)
    sensor_handler.start()

    mqtt_client.loop_start()
    sensor_handler.join()

